mapper.select:
SELECT uid
FROM t_user
WHERE phone = #{phone}

mapper.update:
UPDATE t_user
SET upw = #{upw}
WHERE phone = #{phone}
and uid = #{uid}

mapper.delete:
DELETE FROM t_user
WHERE iuser = #{iuser}
and uid = #{uid}
and phone = #{phone}

mapper.update:
update t_payment
set ibuyer = null
where ibuyer = #{iuser}
